mapper.insert:
insert into user_master
    set user_id = #{id}
    , user_pw = #{pwd}
    , user_name = #{name}
    , user_email = #{email}
    , user_gb = #{gb}
    , user_login_type = #{loginType}

mapper.update:
update user_master
    set user_id = #{id}
    , user_pw = #{pw}
    , user_name = #{name}
    , user_gender = #{gender}
    , user_phone = #{phone}
    , user_pic = #{pic}
    , user_email = #{email}
    , user_gb = #{gb}
    , user_login_type = #{loginType}
where user_seq = #{seq}

mapper.select:
select token_email as email , token_data as token , input_dt as inputDt, end_dt as endDt
from email_token
where token_data = #{token}
